import React from 'react';
import style from './App.module.css';
import Card from './components/Card.jsx';
import Cards from './components/Cards.jsx';
import SearchBar from './components/SearchBar.jsx';
import data, { Cairns } from './data.js';

function App() {
  const [state,setState]= React.useState([
    {"coord":{"lon":-56.1674,"lat":-34.8335},"weather":[{"id":800,"main":"Clear","description":"clear sky","icon":"01d"}],"base":"stations","main":{"temp":9.01,"feels_like":6.5,"temp_min":8.84,"temp_max":9.01,"pressure":1025,"humidity":87},"visibility":10000,"wind":{"speed":4.63,"deg":30},"clouds":{"all":0},"dt":1660222505,"sys":{"type":1,"id":8687,"country":"UY","sunrise":1660213764,"sunset":1660252230},"timezone":-10800,"id":3441575,"name":"Montevideo","cod":200}
  ]);
  return (

    <div className={style.app}>
      <header className={style.header}>        
        <SearchBar onSearch={(ciudad) => alert(ciudad)} />
      </header>
      <main className={style.main}>
      <section className={style.mainCity}>
        <Card
          max={Cairns.main.temp_max}
          min={Cairns.main.temp_min}
          name={Cairns.name}
          img={Cairns.weather[0].icon}
          onClose={() => alert(Cairns.name)}
          main={true}
        />
      </section>

      <section className={style.reelCities}> 
       <Cards cities={state}        />
      </section>
      </main>
    </div>
  );
}

export default App;
